INSERT INTO articles VALUES ('0e631372-f302-4960-80db-bd2583817fbc', 'scholz-e-eleito-novo-chanceler-alemao-e-encerra-era-merkel', 'Scholz é eleito novo chanceler alemão e encerra era Merkel', NULL, NULL, NULL, '<p><strong>Parlamento oficializa sucessão. Social-democrata vai liderar governo de coalizão com verdes e liberais e assume em momento de crise, com quarta onda da pandemia castigando o país. Merkel deixa o poder após 16 anos.</strong> </p>
